import React, { useState } from 'react';
import './form.css';

const Form = () => {
    const [user, setUser] = useState({
        Name: '',
        Email: '',
        Contact: '',
        message: ''
    });

    let name, value;

    const data = (e) => {
        name = e.target.name;
        value = e.target.value;
        setUser({ ...user, [name]: value });
    };

    const displayRazorpay = async (amount) => {
        // Your Razorpay code here
        const options = {
            key: 'YOUR_RAZORPAY_KEY', // Replace with your Razorpay API key
            amount: amount * 100, // Amount in paise (e.g., 1000 means ₹10.00)
            currency: 'INR',
            name: 'Your Company Name',
            description: 'Donation for a cause',
            image: 'URL_TO_YOUR_COMPANY_LOGO',
            handler: function (response) {
                alert(`Payment ID: ${response.razorpay_payment_id}`);
                alert('Payment Successful');
    
                // Now that the payment is successful, you can proceed with the form submission here
                // You can call the form submission function or code here
            },
        };
    
        const razorpayInstance = new Razorpay(options);
        razorpayInstance.open();
    };

    const getdata = async (e) => {
        e.preventDefault();
        
        // Call the Razorpay function before form submission
        displayRazorpay(1000);
        
        // Your form submission code here
        const { Name, Email, Contact, message } = user;
        const options = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                Name,
                Email,
                Contact,
                message
            })
        };

        const res = await fetch(
            'https://donationfirebase-default-rtdb.firebaseio.com/UserData.json',
            options
        );

        console.log(res);

        if (res) {
            alert("Message Sent");
        } else {
            alert("Error Occurred");
        }
    };

    return (
        <>
            <div className='form'>
                <div className='container'>
                    <form method='POST'>
                        <input type='text' name='Name' placeholder='Your Name' value={user.Name} autoComplete='off' required onChange={data}></input>
                        <input type='email' name='Email' placeholder='Email-id' value={user.Email} autoComplete='off' required onChange={data}></input>
                        <input type='text' name='Contact' placeholder='Contact no.' value={user.Contact} autoComplete='off' required onChange={data}></input>
                        <textarea name='message' placeholder='Enter Your Message' value={user.message} autoComplete='off' required onChange={data}></textarea>
                        
                        {/* Single button that combines both actions */}
                        <button onClick={getdata}>Submit and Donate</button>
                    </form>
                </div>
            </div>
        </>
    );
};

export default Form;
