import React, { useState } from 'react';
import './form.css';

const Form = () => {
    const [user, setUser] = useState({
        Name: '',
        Email: '',
        Contact: '',
        message: ''
    });

    const data = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const loadScript = (src) => {
        return new Promise((resolve, reject) => {
            const script = document.createElement('script');
            script.src = src;
            script.onload = resolve;
            script.onerror = reject;
            document.body.appendChild(script);
        });
    };

    const displayRazorpay = async (amount, successCallback) => {
        try {
            await loadScript('https://checkout.razorpay.com/v1/checkout.js');
            const options = {
                // Razorpay options here
                key:'rzp_test_OlgK0vFCXKjoRp',
                currency: "INR",
                amount:amount * 100,
                name: "Durgesh",
                description: "Thanks For Donating",
                handler: function (response) {
                    alert(response.razorpay_payment_id);
                    alert("Payment Successful");
                    // After successful payment, call the success callback
                    successCallback();
                }
            };
            const paymentObject = new window.Razorpay(options);
            paymentObject.open();
        } catch (error) {
            alert("Failed to load Razorpay SDK");
        }
    };

    const submitForm = async () => {
        const { Name, Email, Contact, message } = user;
        if (!Name || !Email || !Contact || !message) {
            alert("Please fill in all fields.");
            return;
        }

        // Call the Razorpay function to initiate the payment and provide a callback function
        displayRazorpay(1000, () => {
            // Proceed with the form submission
            const options = {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    Name,
                    Email,
                    Contact,
                    message
                })
            };
            try {
                fetch('https://donationfirebase-default-rtdb.firebaseio.com/UserData.json', options)
                    .then((res) => {
                        if (res.ok) {
                            alert("Message Sent");
                        } else {
                            alert("Error Occurred");
                        }
                    })
                    .catch((error) => {
                        alert("An error occurred while submitting the form.");
                        console.error(error);
                    });
            } catch (error) {
                alert("An error occurred while submitting the form.");
                console.error(error);
            }
        });
    };

    return (
        <>
            <div className='form'>
                <div className='container'>
                    <form method='POST'>
                        <input type='text' name='Name' placeholder='Your Name' value={user.Name} autoComplete='off' required onChange={data}></input>
                        <input type='email' name='Email' placeholder='Email-id' value={user.Email} autoComplete='off' required onChange={data}></input>
                        <input type='text' name='Contact' placeholder='Contact no.' value={user.Contact} autoComplete='off' required onChange={data}></input>
                        <textarea name='message' placeholder='Enter Your Message' value={user.message} autoComplete='off' required onChange={data}></textarea>
                        <button onClick={submitForm}>Submit and Donate</button>
                    </form>
                </div>
            </div>
        </>
    );
};

export default Form;
